import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [planetList, setPlanetList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  async function getMovies() {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://swapi.dev/api/films/");
      if (!response.ok) {
        throw new Error("Something went wrong !");
      }

      const data = await response.json();
      // eslint-disable-next-line no-unused-expressions
      const selectedData = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          release_date: movieData.release_date,
          opening_crawl: movieData.opening_crawl,
        };
      });
      setPlanetList(selectedData);
    } catch(error) {
      setError(error.message);
    }
    setIsLoading(false);
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={getMovies}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && planetList.length > 0 && (
          <MoviesList movies={planetList} />
        )}
        {!isLoading && planetList.length === 0 && "Movie Not Found"}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && "Loading ... "}
      </section>
    </React.Fragment>
  );
}

export default App;
