import React, { useCallback, useEffect, useState } from "react";

import MoviesList from "./components/MoviesList";
import AddMovie from "./components/AddMovie";
import "./App.css";

function App() {
  const [planetList, setPlanetList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);


  const getMovies=useCallback(async()=>{
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://react-http-project-aedbc-default-rtdb.asia-southeast1.firebasedatabase.app/movies.json");
      if (!response.ok) {
        throw new Error("Something went wrong !");
      }

      const data = await response.json();
      // eslint-disable-next-line no-unused-expressions
      const selectedData = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          release_date: movieData.release_date,
          opening_crawl: movieData.opening_crawl,
        };
      });
      setPlanetList(selectedData);
    } catch(error) {
      setError(error.message);
    }
    setIsLoading(false);
  },[]);

  useEffect(()=>{
    getMovies();
 },[getMovies]);

 async function addMovieHandler(movie) {
  const response=fetch("https://react-http-project-aedbc-default-rtdb.asia-southeast1.firebasedatabase.app/movies.json",{
    method:"POST",
    body:movie,
    headers:{
      'Content-Type':'application/json'
    }
  });

  const data= await response.json();
  console.log("ðŸš€ ~ addMovieHandler ~ data:", data);

}

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={getMovies}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && planetList.length > 0 && (
          <MoviesList movies={planetList} />
        )}
        {!isLoading && planetList.length === 0 && "Movie Not Found"}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && "Loading ... "}
      </section>
    </React.Fragment>
  );
}

export default App;
